// Scan applies the selector query and scans the result into the given value.
func (cas *CandidateAwardSelect) Scan(ctx context.Context, v any) error {
	if err := cas.prepareQuery(ctx); err != nil {
		return err
	}
	cas.sql = cas.CandidateAwardQuery.sqlQuery(ctx)
	return cas.sqlScan(ctx, v)
}

func (cas *CandidateAwardSelect) sqlScan(ctx context.Context, v any) error {
	aggregation := make([]string, 0, len(cas.fns))
	for _, fn := range cas.fns {
		aggregation = append(aggregation, fn(cas.sql))
	}
	switch n := len(*cas.selector.flds); {
	case n == 0 && len(aggregation) > 0:
		cas.sql.Select(aggregation...)
	case n != 0 && len(aggregation) > 0:
		cas.sql.AppendSelect(aggregation...)
	}
	rows := &sql.Rows{}
	query, args := cas.sql.Query()
	if err := cas.driver.Query(ctx, query, args, rows); err != nil {
		return err
	}
	defer rows.Close()
	return sql.ScanSlice(rows, v)
}
